changeSet.sql:
set @concept_id = 0;
set @member1_concept_id = 0;

select concept_id into @concept_id from concept_name where name = 'Operative Report' and concept_name_type = 'FULLY_SPECIFIED' and locale = 'en' and voided = 0;
select concept_id into @member1_concept_id from concept_name where name = 'OR, Post-operative order' and concept_name_type = 'FULLY_SPECIFIED' and locale = 'en' and voided = 0;

call add_concept_set_members (@concept_id, @member1_concept_id, 20);
