changeSet.sql:
select concept_id into @concept_id from concept_name where name = "APN, Complication of anaesthesia" and concept_name_type = "FULLY_SPECIFIED" and locale = "en" and voided = 0;
set @child1_concept_id = 0;
set @child2_concept_id = 0;
set @child3_concept_id = 0;

select concept_id into @child1_concept_id from concept_name where name ="Unanticipated Difficult Intubation" and concept_name_type ="FULLY_SPECIFIED" and locale ="en" and voided = 0;
select concept_id into @child2_concept_id from concept_name where name ="Unanticipated Movement to ICU/HDU" and concept_name_type ="FULLY_SPECIFIED" and locale ="en" and voided = 0;
select concept_id into @child3_concept_id from concept_name where name ="Unanticipated Reoperation" and concept_name_type ="FULLY_SPECIFIED" and locale ="en" and voided = 0;     
select concept_id into @concept_id1 from concept_name where name = "Other" and concept_name_type ="FULLY_SPECIFIED" and locale ="en" and voided = 0;     

UPDATE concept_answer set sort_weight = 15 where answer_concept=@concept_id1 and concept_id=@concept_id; 

call add_concept_answer(@concept_id, @child1_concept_id, 12);
call add_concept_answer(@concept_id, @child2_concept_id, 13);
call add_concept_answer(@concept_id, @child3_concept_id, 14);

select concept_id into @concept_id from concept_name where name = "Post-operative Anaesthesia Note" and 
concept_name_type = 'FULLY_SPECIFIED' and locale = "en" and voided = 0;

select concept_id into @concept_id2 from concept_name where name ="APN, Anaesthetic post-operative notes" and concept_name_type =
'FULLY_SPECIFIED' and locale = 'en' and voided = 0;
UPDATE concept_set SET sort_weight=1110 WHERE concept_id=@concept_id2 and concept_set=@concept_id;

set @member1_concept_id = 0;
select concept_id into @member1_concept_id from concept_name where name ="APN, Anaesthetic post-operative orders" and concept_name_type = 'FULLY_SPECIFIED' and locale = 'en' and voided = 0;

call add_concept_set_members(@concept_id, @member1_concept_id, 1100);

SELECT concept_source_id INTO @source_id FROM concept_reference_source where name = "CEIL";

INSERT INTO concept_reference_term (creator,code,concept_source_id,uuid,date_created) VALUES (1,"149086",@source_id,uuid(),now());
INSERT INTO concept_reference_term (creator,code,concept_source_id,uuid,date_created) VALUES (1,"156221",@source_id,uuid(),now());
INSERT INTO concept_reference_term (creator,code,concept_source_id,uuid,date_created) VALUES (1,"166099",@source_id,uuid(),now());

call CREATE_REFERENCE_MAPPING_CEIL("Drug Reaction","149086");
