changeSet.sql:
select concept_id into @concept_id from concept_name where name = "PSA, Referrals ordered" and
concept_name_type = "FULLY_SPECIFIED" and locale = "en" and voided = 0;
set @child1_concept_id = 0;
set @child2_concept_id = 0;
set @child3_concept_id = 0;
set @child4_concept_id = 0;
set @child5_concept_id = 0;
set @child6_concept_id = 0;
set @child7_concept_id = 0;

select concept_id into @child1_concept_id from concept_name where name ="No" and concept_name_type ="FULLY_SPECIFIED" and locale ="en" and voided = 0;
select concept_id into @child2_concept_id from concept_name where name ="BMRC MLO" and concept_name_type ="FULLY_SPECIFIED" and locale ="en" and voided = 0;
select concept_id into @child3_concept_id from concept_name where name ="Sanaa MLO" and concept_name_type ="FULLY_SPECIFIED" and locale ="en" and voided = 0;
select concept_id into @child4_concept_id from concept_name where name ="Aden MLO" and concept_name_type ="FULLY_SPECIFIED" and locale ="en" and voided = 0;
select concept_id into @child5_concept_id from concept_name where name ="Gaza MLO" and concept_name_type ="FULLY_SPECIFIED" and locale ="en" and voided = 0;
select concept_id into @child6_concept_id from concept_name where name ="Self-referral" and concept_name_type ="FULLY_SPECIFIED" and locale ="en" and voided = 0;
select concept_id into @child7_concept_id from concept_name where name ="Other" and concept_name_type ="FULLY_SPECIFIED" and locale ="en" and voided = 0;

call add_concept_answer(@concept_id, @child1_concept_id, 1);
call add_concept_answer(@concept_id, @child2_concept_id, 2);
call add_concept_answer(@concept_id, @child3_concept_id, 3);
call add_concept_answer(@concept_id, @child4_concept_id, 4);
call add_concept_answer(@concept_id, @child5_concept_id, 5);
call add_concept_answer(@concept_id, @child6_concept_id, 6);
call add_concept_answer(@concept_id, @child7_concept_id, 7);

select concept_id into @concept_id from concept_name where name = "PSA, Discharge Assessment" and concept_name_type = 'FULLY_SPECIFIED' and locale = "en" and voided = 0;

select concept_id into @concept_id1 from concept_name where name ="PSA, Comment on the referral" and concept_name_type ='FULLY_SPECIFIED' and locale = 'en' and voided = 0;
select concept_id into @concept_id2 from concept_name where name ="PSA, Prescribers: Comment on the process of care and discharge" and concept_name_type = 'FULLY_SPECIFIED' and locale = 'en' and voided = 0;

UPDATE concept_set SET sort_weight=5 WHERE concept_id=@concept_id1 and concept_set=@concept_id;
UPDATE concept_set SET sort_weight=6 WHERE concept_id=@concept_id2 and concept_set=@concept_id;


set @member1_concept_id = 0;
select concept_id into @member1_concept_id from concept_name where name ="PSA, Referrals ordered" and concept_name_type = 'FULLY_SPECIFIED' and locale = 'en' and voided = 0;
call add_concept_set_members(@concept_id, @member1_concept_id, 4);
