changeSet.sql:
set @concept_id = 0;
set @concept_short_id = 0;
set @concept_full_id = 0;
set @count = 0;
set @uuid = NULL;

call add_concept(@concept_id,@concept_short_id,@concept_full_id,"3D","3D","N/A","Misc",false);
call add_concept(@concept_id,@concept_short_id,@concept_full_id,"3D-workflow","3D-workflow","N/A","Misc",false);
call add_concept(@concept_id,@concept_short_id,@concept_full_id,"3D-outcome","3D-outcome","N/A","Misc",true);
call add_concept(@concept_id,@concept_short_id,@concept_full_id,"Active 3D","Active 3D","N/A","Misc",false);

select concept_id into @3D_outcome_concept_id from concept_name where name = "3D-outcome" and
    concept_name_type = 'FULLY_SPECIFIED' and locale = "en" and voided = 0;

set @child1_concept_id = 0;
set @child2_concept_id = 0;
set @child3_concept_id = 0;
set @child4_concept_id = 0;
set @child5_concept_id = 0;

select concept_id into @child1_concept_id from concept_name where name ="Defaulter" and concept_name_type =
                                                                                        'FULLY_SPECIFIED' and locale = 'en' and voided = 0;
select concept_id into @child2_concept_id from concept_name where name ="Dismissal" and concept_name_type =
                                                                                        'FULLY_SPECIFIED' and locale = 'en' and voided = 0;
select concept_id into @child3_concept_id from concept_name where name ="Refused" and concept_name_type =
                                                                                    'FULLY_SPECIFIED' and locale = 'en' and voided = 0;
select concept_id into @child4_concept_id from concept_name where name ="MBA" and concept_name_type =
                                                                                      'FULLY_SPECIFIED' and locale = 'en' and voided = 0;
select concept_id into @child5_concept_id from concept_name where name ="Death" and concept_name_type =
                                                                                  'FULLY_SPECIFIED' and locale = 'en' and voided = 0;

call add_concept_set_members(@3D_outcome_concept_id, @child1_concept_id, 1);
call add_concept_set_members(@3D_outcome_concept_id, @child2_concept_id, 2);
call add_concept_set_members(@3D_outcome_concept_id, @child3_concept_id, 3);
call add_concept_set_members(@3D_outcome_concept_id, @child4_concept_id, 4);
call add_concept_set_members(@3D_outcome_concept_id, @child5_concept_id, 5);

select concept_id into @3D_concept_id from concept_name where name = '3D' and concept_name_type
    = 'FULLY_SPECIFIED' and locale = 'en' and voided = 0;
select concept_id into @3D_outcome_concept_id from concept_name where name = '3D-outcome' and
    concept_name_type = 'FULLY_SPECIFIED' and locale = 'en' and voided = 0;
select user_id into @creator_concept_id from users where username='superman';

INSERT INTO program(concept_id,outcomes_concept_id,creator,date_created,name,description,uuid)
VALUES (@3D_concept_id,@3D_outcome_concept_id,@creator_concept_id,now(),'3D','3D Program',uuid());

preConditions.sqlCheck:
select count(*) from program_workflow pw join concept_name cn on pw.concept_id = cn.concept_id
where pw.concept_id = (select concept_id from concept_name where name = '3D-workflow' and
    concept_name_type = 'FULLY_SPECIFIED') and concept_name_type = 'FULLY_SPECIFIED';

changeSet.sql:
select program_id into @3D_program_workflow from program where name='3D';
select concept_id into @3D_workflow_concept_id from concept_name where name ="3D-workflow" and
    concept_name_type = 'FULLY_SPECIFIED' and locale = 'en' and voided = 0;
select user_id into @creator_concept_id from users where username='superman';

INSERT INTO program_workflow (program_id , concept_id , creator , date_created , retired , uuid)
VALUES (@3D_program_workflow,@3D_workflow_concept_id,@creator_concept_id, now(),0, uuid());

select concept_id into @3D_workflow_concept_id from concept_name where name = "3D-workflow" and
    concept_name_type = 'FULLY_SPECIFIED' and locale = 'en' and voided = 0;
select program_workflow_id into @program_workflow_id from program_workflow where concept_id =
                                                                                 @3D_workflow_concept_id;

select concept_id into @active_3D_concept_id from concept_name where name = "Active 3D"
                                                                      and concept_name_type = 'FULLY_SPECIFIED' and locale = 'en' and voided = 0;
select concept_id into @discharge_concept_id from concept_name where name = "Discharge"
                                                                        and concept_name_type = 'FULLY_SPECIFIED' and locale = 'en' and voided = 0;

insert into program_workflow_state
(program_workflow_id,concept_id,initial,terminal,creator,date_created,retired ,uuid) values
                                                                                         (@program_workflow_id,@active_3D_concept_id,0,0,1,now(),0,uuid()),
                                                                                         (@program_workflow_id,@discharge_concept_id,0,0,1,now(),0,uuid());
